import React from "react";
import "./style.css";
import Navbar from "../../Navbar";
import Footer from "../../Footer";

class AddStartUp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      logo: "",
      mail: "",
      description: "",
      web: "",
      twitter: "",
      instagram: "",
    };
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.name === 'isGoing' ? target.checked : target.value;
    this.setState(
      {
        [name]: value
      },
      () => {
        console.log(this.state);
      }
    );
  }

  render() {
    return (
      <div className="formWrapper">
        <div className="inputWrapper">
          <label className="label" for="name">Nombre</label>
          <input
            className="input"
            type="text"
            name="name"
            placeholder="Nombre"
            onChange={(e)=>this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label for="logo">Logo</label>
          <input
            type="text"
            name="logo"
            placeholder="Link a imagen"
            onChange={(e)=>this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label for="mail">Mail</label>
          <input
            type="text"
            name="mail"
            placeholder="Mail"
            onChange={(e)=>this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label for="description">Descripci√≥n</label>
          <input
            type="text"
            name="description"
            onChange={(e)=>this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label for="web">Web</label>
          <input
            type="text"
            name="web"
            placeholder="Web (opcional)"
            onChange={(e)=>this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label for="twitter">Twitter</label>
          <input
            type="text"
            name="twitter"
            placeholder="Twitter (opcional)"
            onChange={(e)=>this.handleInputChange(e)}
          ></input>
        </div>
        <button className="submitButton">Enviar</button>
      </div>
    );
  }
}

export default AddStartUp;
