import React from "react";
import "./style.css";
import Navbar from "../../Navbar";
import Footer from "../../Footer";
import data from "../../data/data.json";

class AddStartUp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      logo: "",
      mail: "",
      description: "",
      web: "",
      twitter: "",
      instagram: "",
      estaOk: "",
      showMessage: false,
    };
  }

  handleInputChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value,
    });
  }

  handleClick() {
   
    const { name, mail, logo, description } = this.state;
    const isValid = name && mail && logo && description ? true : false;
    this.setState({ estaOk: isValid, showMessage: true }, () => {
      if (isValid) {
        let state = {
          name: "Example",
          age: "21",
          description: "Some text here.",
        };

        this.saveStartUp(JSON.stringify(state));
      }
    });
  }

  saveStartUp(item) {
    const editJsonFile = require("edit-json-file");

    // If the file doesn't exist, the content will be an empty object by default.
    let file = editJsonFile("/home/gonzalo/Desarrollo/repos/courseIt/React/startups-callbacks/src/components/data/data.json");

    // Set a couple of fields
    file.set("planet", "Earth");
    file.set("city\\.name", "anytown");
    file.set("name.first", "Johnny");
    file.set("name.last", "B.");
    file.set("is_student", false);

    // Output the content
    console.log(file.get());
    // { planet: 'Earth',
    //   city.name: 'anytown',
    //   name: { first: 'Johnny', last: 'B.' },
    //   is_student: false }

    // Save the data to the disk
    file.save();

    // Reload it from the disk
    file = editJsonFile(`${__dirname}/foo.json`, {
      autosave: true,
    });

    // Get one field
    console.log(file.get("name.first"));
    // => Johnny

    // This will save it to disk
    file.set("a.new.field.as.object", {
      hello: "world",
    });
    // const editJsonFile = require("edit-json-file")
    // let file = editJsonFile(data)
    // file.set(item)
    // file.save()
  }

  render() {
    const { estaok, showMessage } = this.state;
    return (
      <div className="formWrapper">
        {this.state.showMessage && (
          <p>{this.state.estaOk ? "Todo salio bien" : "Todo salio mal"}</p>
        )}
        <div className="inputWrapper">
          <label className="label">
            Nombre
            <input
              className="input"
              type="text"
              name="name"
              placeholder="Nombre"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Logo
            <input
              className="input"
              type="text"
              name="logo"
              placeholder="Link a imagen"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Mail
            <input
              className="input"
              type="text"
              name="mail"
              placeholder="Mail"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Descripción
            <input
              className="input"
              type="text"
              name="description"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Web
            <input
              className="input"
              type="text"
              name="web"
              placeholder="Web (opcional)"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Twitter
            <input
              className="input"
              type="text"
              name="twitter"
              placeholder="Twitter (opcional)"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Instagram
            <input
              className="input"
              type="text"
              name="instagram"
              placeholder="Instagram (opcional)"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <button className="submitButton" onClick={() => this.handleClick()}>
          Enviar
        </button>
      </div>
    );
  }
}

export default AddStartUp;
