import React from 'react';
import './style.css';


class Search extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      inputValue: ''
    }
  }

  handleChange(e) {
    console.log(this.inputValue)

    this.setState(
      {
        inputValue: e.target.value
      }
    )
  }
  render() {
    return (
      <div className="search">
        <input onChange={(e) => this.handleChange(e)} class="inputSearch" type="text" placeholder="Buscar..."></input>
        <p style={{color:"blue"}}>{this.state.inputValue}</p>
        
      </div>
    );
  }
}

export default Search;
