import React from "react";
import "./style.css";
import Card from "./Card";

class Cards extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      startups: this.props.cards,
    };
  }

  componentDidUpdate() {
    let arrayFiltered = this.state.startups.filter((filtered) =>
      filtered.title.toLowerCase().includes("end".toLowerCase())
    );
  }
  render() {
    
    console.log(arrayFiltered);
    return (
      <div className="CardWrapper">
        {arrayFiltered.map((startup, key) => {
          return <Card startup={startup} />;
        })}
      </div>
    );
  }
}

export default Cards;
