import React from "react";
import "./style.css";
import Navbar from "../../Navbar";
import Footer from "../../Footer";

class AddStartUp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      logo: "",
      mail: "",
      description: "",
      web: "",
      twitter: "",
      instagram: "",
    };
  }

  handleInputChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState(
      {
        [name]: value,
      },
      () => {
        console.log(this.state);
      }
    );
  }

  handleClick() {
    const { name, mail, logo, description } = this.state;
    if (name && mail && logo && description) {
      this.setState({ estaOk: true, showMessage: true });
    } else {
      this.setState({ estaOk: false, showMessage: true });
    }
  }

  render() {
    return (
      <div className="formWrapper">
        <div className="inputWrapper">
          <label className="label" for="name">
            Nombre
          </label>
          <input
            className="input"
            type="text"
            name="name"
            placeholder="Nombre"
            onChange={(e) => this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label className="label" for="logo">
            Logo
          </label>
          <input
            className="input"
            type="text"
            name="logo"
            placeholder="Link a imagen"
            onChange={(e) => this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label className="label" for="mail">
            Mail
          </label>
          <input
            className="input"
            type="text"
            name="mail"
            placeholder="Mail"
            onChange={(e) => this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label className="label" for="description">
            Descripción
          </label>
          <input
            className="input"
            type="text"
            name="description"
            onChange={(e) => this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label className="label" for="web">
            Web
          </label>
          <input
            className="input"
            type="text"
            name="web"
            placeholder="Web (opcional)"
            onChange={(e) => this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label className="label" for="twitter">
            Twitter
          </label>
          <input
            className="input"
            type="text"
            name="twitter"
            placeholder="Twitter (opcional)"
            onChange={(e) => this.handleInputChange(e)}
          ></input>
        </div>
        <div className="inputWrapper">
          <label className="label" for="instagram">
            Instagram
          </label>
          <input
            className="input"
            type="text"
            name="instagram"
            placeholder="Instagram (opcional)"
            onChange={(e) => this.handleInputChange(e)}
          ></input>
        </div>

        {this.state.showMessage && (
          <p>{this.state.estaOk ? "Todo salio bien" : "Todo salio mal"}</p>
        )}
        <button className="submitButton" onClick={() => this.handleClick()}>
          Enviar
        </button>
      </div>
    );
  }
}

export default AddStartUp;
