        )}
        <div className="inputWrapper">
          <label className="label">
            Nombre
            <input
              className="input"
              type="text"
              name="name"
              placeholder="Nombre"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Logo
            <input
              className="input"
              type="text"
              name="logo"
              placeholder="Link a imagen"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Mail
            <input
              className="input"
              type="text"
              name="mail"
              placeholder="Mail"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Descripci√≥n
            <input
              className="input"
              type="text"
              name="description"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Web
            <input
              className="input"
              type="text"
              name="web"
              placeholder="Web (opcional)"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Twitter
            <input
              className="input"
              type="text"
              name="twitter"
              placeholder="Twitter (opcional)"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <div className="inputWrapper">
          <label className="label">
            Instagram
            <input
              className="input"
              type="text"
              name="instagram"
              placeholder="Instagram (opcional)"
              onChange={(e) => this.handleInputChange(e)}
            />
          </label>
        </div>
        <button className="submitButton" onClick={() => this.handleClick()}>
          Enviar
        </button>
      </div>
    );
  }
}

export default AddStartUp;
