import React from "react";
import "./App.css";
import Navbar from "./components/Navbar";
import Cards from "./components/Cards";
import Footer from "./components/Footer";
import { Link } from "react-router-dom";
import data from './components/data/data.json'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: "",
      cards: []
    };
  }

  handleData(inputValue) {
    this.setState({ inputValue: inputValue });
  }

  componentDidMount(){
    this.setState({cards:  JSON.stringify(data)})
  }

  render() {
    console.table(data)
    localStorage.setItem("startUpList", JSON.stringify(data));

    const { inputValue, cards } = this.state;
    return (
      <div className="App">
        <Navbar
          getData={(inputValue) => this.handleData(inputValue)}
          name="STARTUP ARGENTINA"
          search={true}
        />
        <Cards filter={inputValue} cards={cards} />
        <Footer creator="Gonzalito" />
        <Link to={{ pathname: "/prueba", cards: cards }}>Ir a Prueba</Link>
      </div>
    );
  }
}

export default App;
