import React from 'react';
import './style.css';


class Card extends React.Component {
  render() {
    return (
      <div className="Card">
        <img src={this.props.item.img}></img>
        <div class="InfoCard">
          <span class="Title">{this.props.item.title}</span>
          <span class="Description">{this.props.item.description}</span>
        </div>
      </div>
    );
  }
}

export default Card;
